import React, { useState } from 'react';
import { connect } from 'react-redux'

import FormInput from '../form-input/form-input.component';
import CustomButton from '../custom-button/custom-button.component';

import {
  SignInContainer,
  SignInTitle,
  ButtonsBarContainer
} from './sign-in.styles';
import { googleSignInStart, emailSignInStart } from '../../redux/user/user.actions';
//use a class component to store signed-in data

const SignIn = ({ emailSignInStart,googleSignInStart }) => {
  const [ userCredentials, setCredentials ] = useState({ email: '', password: '' })

  //dont let the form function, using during testing
  //destruction of the props will give a better reading exp
  const { email,password } = userCredentials;
  const handleSubmit = async event => {
    event.preventDefault();

    emailSignInStart(email,password);
  };

  //pull info
  const handleChange = event => {
    const { value,name } = event.target;

    setCredentials({ ...userCredentials, [name]:value })
  }

  return (
    <SignInContainer>
      <SignInTitle>I already have an account</SignInTitle>
      <span>Sign in with your email and password</span>

      <form onSubmit={handleSubmit}>
        <FormInput name='email' type='email' handleChange={handleChange} value={email} label='email' required />
        <FormInput name='password' type='password' value={password} handleChange={handleChange} label='password' required />
        <ButtonsBarContainer>
          <CustomButton type='submit'> Sign In </CustomButton>
          <CustomButton type='button' onClick={googleSignInStart} isGoogleSignIn>
          {''}
          Sign in with Google{''}
          </CustomButton>
        </ButtonsBarContainer>
      </form>
    </SignInContainer>
  )
  
}

const mapDispatchToProps = dispatch => ({
  googleSignInStart: () => dispatch(googleSignInStart()),
  emailSignInStart: (email,password) => dispatch(emailSignInStart({ email, password }))
})

export default connect(null, mapDispatchToProps)(SignIn);
