import React from 'react';
import { connect } from 'react-redux'

import FormInput from '../form-input/form-input.component';
import CustomButton from '../custom-button/custom-button.component';

import { SignUpContainer, SignUpTitle } from './sign-up.styles';
import { onSignUpStart } from '../../redux/user/user.sagas';

class SignUp extends React.Component {
  constructor() {
    super();

    this.state = {
      displayName:'',
      email:'',
      password:'',
      confirmPassword:''
    }
  }

  handleSubmit = async event => {
    event.preventDefault();

    const { signUpStart } = this.props;
    const { displayName, email, password, confirmPassword } = this.state;

    if (password !== confirmPassword){
      alert("passwords don't match");
      return;
    }
    onSignUpStart({ displayName, email, password });
  };

  handleChange = event => {
    const { name, value } = event.target;

    this.setState({[name]:value});
  }

  render () {
    const { displayName, email, password, confirmPassword } = this.state;
    return(
      <SignUpContainer>
        <SignUpTitle className='title'>I do not have an account</SignUpTitle>
        <span>Sign up with your email and password</span>
        <form className="sign-up-form" onSubmit={this.handleSubmit}>
          <FormInput 
            type = 'text'
            name = 'displayName'
            value = {displayName}
            onChange = {this.handleChange}
            label = 'Display Name'
            required
          />
          <FormInput 
            type = 'email'
            name = 'email'
            value = {email}
            onChange = {this.handleChange}
            label = 'Email'
            required
          />
          <FormInput 
            type = 'password'
            name = 'password'
            value = {password}
            onChange = {this.handleChange}
            label = 'Password'
            required
          />
          <FormInput 
            type = 'password'
            name = 'confirmPassword'
            value = {confirmPassword}
            onChange = {this.handleChange}
            label = 'Confirm Password'
            required
          />
          <CustomButton type='submit'>SIGN UP</CustomButton>
        </form>
      </SignUpContainer>
    )
  }
}

const mapDispatchToProps = dispatch => ({
  signUpStart: userCredentials => dispatch(onSignUpStart(userCredentials))
})

export default connect(null,mapDispatchToProps)(SignUp);
